var Validator={emailRegex:/^\s*[\w\-\+_]+(\.[\w\-\+_]+)*\@[\w\-\+_]+\.[\w\-\+_]+(\.[\w\-\+_]+)*\s*$/,passwordRegex:/^[a-z]+$/,numberRegex:/^[0-9]+$/,countryRegex:/[a-zA-Z]{2,}/,valid:function(e,r){return this[e+"Regex"].exec(r)},email_error_message:"You need to provide a valid email address",password_error_message:"Passwords must be all lowercase letters",number_error_message:"Must be a number",country_error_message:"Must be a country name",validate_field:function(e){var r=e.data("validate-as")||e.attr("type");console.log(r);var t=e.closest(".form-input").find(".error-msg");return this.valid(r,e.val())?(t.text(""),!0):(t.text(this[r+"_error_message"]),!1)}};$(function(){$("form").focusout(function(e){var r=$(e.target);r.is("input")&&r.val()&&Validator.validate_field(r)}),$("form").submit(function(e){var r=$(e.target).closest("form").find("input").not(":input[type=submit]"),t=$.grep(r,function(e){return!Validator.validate_field($(e))}).length;t>0&&e.preventDefault()})});