function Display(o){for(this.board=o;!o.isFinished();)this.showBoard(),console.log(this.requestMove());this.showBoard()}Display.prototype.showBoard=function(){this.verticalDivider();for(var o=0;o<board.gridSize;o++){for(var r="|",i=0;i<board.gridSize;i++)r+=board.cellAtPos(o,i).show(),r+="|";console.log(r),this.verticalDivider()}},Display.prototype.verticalDivider=function(){for(var o="",r=0;r<board.gridSize;r++)o+="_";console.log(o)},Display.prototype.requestMove=function(){var o=parseInt(prompt("row : ")),r=parseInt(prompt("column : "));return board.makeMove(o,r),[o,r]};