function Board(r,t){var n,i;this.init=function(r,t){n=$(r),i=new Array(t);for(var a=0;a<t;a++)i[a]=new Array(t);return this},this.display=function(){n.empty();for(var r=0;r<i.length;r++){for(var t="<tr>",a=0;a<i[r].length;a++)t+='<td class="square" id="'+a+"-"+r+'"></td>';t+="</tr>",n.append(t)}},this.init(r,t)}