function getRandomArbitrary(t){return Math.floor(Math.random()*t)}function Board(t,o){return this.gridSize=t,this.grid=[],this.bombCount=o,this.setupGrid(),this.generateBombs(),this}Board.prototype.setupGrid=function(){for(var t=0;t<this.gridSize;t++){this.grid[t]=[];for(var o=0;o<this.gridSize;o++)this.grid[t][o]=new Cell}},Board.prototype.generateBombs=function(){for(var t=0;t<this.bombCount;t++){var o=this.randomPosition(),r=this.randomPosition(),i=this.cellAtPos(o,r);i.bomb&&t--,i.bomb=!0,this.updateCellCounts(o,r)}},Board.prototype.updateCellCounts=function(t,o){for(var r=t-1;t+1>r;r++)for(var i=o-1;o+1>i;i++){var n=this.cellAtPos(r,i);n&&n.count++}},Board.prototype.randomPosition=function(){return getRandomArbitrary(this.gridSize)},Board.prototype.cellAtPos=function(t,o){return this.grid[t][o]};